import React from "react";
import { useState } from 'react';

export default function Tags() {
    const [isTagActive, setIsTagActive] = useState(false);
    const handleClick = () => setIsTagActive(!isTagActive);

    return (
       <div className={isTagActive ? "tag-container active" : "tag-container"} onClick={handleClick}>
            <p className="tag-txt">All</p>
       </div>
    )
}
