import React from "react";
import { useState, useEffect } from 'react';

import Video from "../components/video";

export default function HomeVideoSectionPage() {
    const youtubeVideoAPI = "https://www.googleapis.com/youtube/v3/videos?";
    const youtubeAPIKey = "AIzaSyAvWZ9W7xA5cmUkjPVZxIC1tbLWgh9abiY";
    const regionCode = "SA";

    const [videos, setVideos] = useState([]);

    const VideoFetchAPI = () => {
        fetch(youtubeVideoAPI + new URLSearchParams({
            key: youtubeAPIKey,
            part: 'snippet',
            chart: 'mostPopular',
            maxResults: 2,
            regionCode: regionCode,
        }))
        .then(res => res.json())
        .then(data => {
            console.log(data);
            data.items.forEach(item => ChannelFetchAPI(item))
        })
    }

    const ChannelFetchAPI = (video) => {
        fetch("https://www.googleapis.com/youtube/v3/channels?" + new URLSearchParams({
                key: youtubeAPIKey,
                part: 'snippet',
                id: video.snippet.channelId
            }))
            .then(res => res.json())
            .then(data => {
                video.channelThumbnail = data.items[0].snippet.thumbnails.default.url;
                setVideos(video);
            })
    }

    // useEffect(() => {VideoFetchAPI()});

    return (
        {/* 
            {videos.map((video) => (
                <Video 
                    key={video.id}
                    videoImage={video.snippet.thumbnails.high.url}
                    videoTitle={video.snippet.title}
                    // videoTime={}
                    // videoDate={}
                    // videoViews={}
                    channelImage={video.channelThumbnail}
                    channelName={video.snippet.channelTitle}
                 />
            ))} 
            */}
    )
}
