import React from "react";
import { useState, useEffect } from 'react';

import Video from "../components/video";

export default function HomePage() {
    const youtubeVideoAPI = "https://www.googleapis.com/youtube/v3/videos?";
    const youtubeAPIKey = "AIzaSyAvWZ9W7xA5cmUkjPVZxIC1tbLWgh9abiY";
    const regionCode = "SA";
    const [videos, setVideos] = useState([]);
    useEffect(() => {
        fetch(youtubeVideoAPI + new URLSearchParams({
            key: youtubeAPIKey,
            part: 'snippet',
            chart: 'mostPopular',
            maxResults: 50,
            regionCode: regionCode,
        }))
        .then(res => res.json())
        data.items.forEach(item => {
            getChannelIcon(item);
        })
        // .then(data => setVideos(data.items));
    }, []);

    useEffect(() => {
        fetch("https://www.googleapis.com/youtube/v3/channels?" + new URLSearchParams({
            key: "AIzaSyAvWZ9W7xA5cmUkjPVZxIC1tbLWgh9abiY",
            part: 'snippet',
            id: videos.map((video) => {video.snippet.channelId})
        }))
        .then(res => res.json())
        .then(data => {
            video_data.channelThumbnail = data.items[0].snippet.thumbnails.default.url;
            makeVideoCard(video_data);
        })
    }, []);

    console.log(videos[0])

    return (
        <div className="home-page">
            {videos.map((video) => (
                <Video 
                    key={video.id}
                    videoImage={video.snippet.thumbnails.high.url}
                    videoTitle={video.snippet.title}
                    // videoTime={}
                    // videoDate={}
                    // videoViews={}
                    // channelImage={}
                    channelName={video.snippet.channelTitle}
                 />
            ))}
        </div>
    )
}
